const initialState = {

}

export default(state = initialState,action)=>{
    const {user} = state;
    const {payload} = action;
    switch (action.type){
        case 'CREATE_USER':
            return {...state, payload}
        case 'LOG':
            return {...state,isLogged:payload}
        case "REQUEST":
            return state;
        default: return state
    }
}
