import React from "react";
import { connect } from 'react-redux';
import { deleteArticle } from '../../actions/deleteArticle'
import { bindActionCreators } from 'redux';
import { makeStyles } from '@material-ui/core/styles';
import { Link } from 'react-router-dom';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import Article from '../Article/Article';

const useStyles = makeStyles(theme => ({
  button: {
    marginBottom: theme.spacing(2),
  },
  input: {
    display: 'none',
  },
  links:{
    textDecoration:"none",
    color: "white"
}
}));

function Home(props) {
  const classes = useStyles();
  const state = props.articles.Articles;

  const ArticlesList = state.articles.map((item, id)=>{
    return <Article item={item} key={id}/>
  });
  const TagsList = state.tags.map((item, id)=>{
    return <Button key={id}>{item.name}</Button>
  });

    return (
      <Container>
        {state.isLogged ? (
            <Link to="/add" className={classes.links} >
              <Button variant="outlined" color="primary" className={classes.button}>
                Add
              </Button>
            </Link>
          ):(
            <></>
          )}
        <Grid container spacing={3}>
          <Grid item xs={10}>
          <div>{ArticlesList}</div>
          </Grid>
          <Grid item xs={2}>
          <div>{TagsList}</div>
          </Grid>
        </Grid>
      </Container>
    );
}

const mapStateToProps = state =>({
  articles:state
})

const mapDispatchToProps =  dispatch => bindActionCreators({
  addArticle
},dispatch)

export default connect(mapStateToProps, mapDispatchToProps)(Home)
