const initialState = {
    isLogged: true,
    articles:[
        {      
          title:"title",
          body:"body"
        },
        {      
            title:"title",
            body:"body"
        },
    ],
    tags:[
        {name:"1"},
        {name:"2"},
        {name:"3"},
        {name:"4"},
        {name:"5"}
    ],
}

export default(state = initialState,action)=>{
    const {articles} = state;
    const {payload} = action;
    switch (action.type){
        case 'ADD_ARTICLE':
            return {...state,articles:[...articles,payload]}
        case 'CHANGE_ARTICLE':
            return {...state,size:payload}
            case 'CHANGE_ARTICLE':
            return {...state,size:payload}
        case 'DELETE_ARTICLE':
            return {...state,articles:articles.filter((data, i) => i !== action.payload)}
        case "REQUEST":
            return state;
        default: return state
    }
}
