const initialState = {
    articles:[
        { 
          id:1,     
          title:"Lorem ipsum",
          body:"1111111",
          tags:"tag1, tag2",
          author:"123",
          comments:[
                {
                  author:"111",
                  body:"Good article"
                },
                {
                    author:"222",
                    body:"Very good article"
                  },
            ]
        },
    ],
    tags:[
        {name:"tag1"},
        {name:"tag2"},
        {name:"tag3"},
        {name:"tag4"},
        {name:"tag5"}
    ],
}

export default(state = initialState,action)=>{
    const {articles} = state;
    const { tags } = state;
    const {payload} = action;
    switch (action.type){
        case 'ADD_ARTICLE':
            return {...state,articles:[...articles,payload]}
        case 'EDIT_ARTICLE':
            return (articles.map(article=>{
                if(article.id===payload.id){
                    return {...state,articles:{...articles,payload}}
                }else{
                    return state
                }
            }))
        case 'ADD_TAG':
            return {...state,tags:[...tags,payload]}
        case 'ADD_COMMENT':
            return (articles.map(article=>{
                if(article.id===payload.id){
                    return {...state,article:{...article,comments:payload}}
                }else{
                    return state
                }
            }))

        case 'DELETE_ARTICLE':
            return {...state,articles:articles.filter((data) => data.id !== action.payload)}
        case "REQUEST":
            return state;
        default: return state
    }
}
