import React from "react";
import { connect } from 'react-redux'
import { deleteArticle } from '../../actions/deleteArticle'
import { bindActionCreators } from 'redux';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import Article from '../Article/Article'

const useStyles = makeStyles(theme => ({
  button: {
    margin: theme.spacing(1),
  },
  input: {
    display: 'none',
  },
}));

function Home(props) {
  const classes = useStyles();
  const articles = props.articles.Articles.articles;
  const tags = props.articles.Articles.tags;
  const isLogged = props.articles.Articles.isLogged;

  const ArticlesList = articles.map((item, id)=>{
    return <Article item={item} key={id}/>
  });
  const TagsList = tags.map((item, id)=>{
    return <div>{}</div>
  });

    return (
      <Container>
        <Grid container spacing={3}>
        <Grid item xs={10}>
        {
          isLogged ? (
            <Button variant="outlined" color="primary" className={classes.button}>
            Add
          </Button>
        ):(
          <></>
        )

        }
        <div>{ArticlesList}</div>
        </Grid>
        <Grid item xs={2}>
        <div>{TagsList}</div>
        </Grid>
      </Grid>






        </Container>
    );
}

const mapStateToProps = state =>({
  articles:state
})

const mapDispatchToProps =  dispatch => bindActionCreators({
  deleteArticle
},dispatch)

export default connect(mapStateToProps, mapDispatchToProps)(Home)
