import React, {useState} from "react";
import { connect } from 'react-redux';
import { makeStyles } from '@material-ui/core/styles';
import { changeArticle } from '../../actions/changeArticle'
import Container from '@material-ui/core/Container';
import { createStructuredSelector } from 'reselect';
import { getAllArticles } from '../../selectors/getAllArticles';
import { getLogged } from '../../selectors/getLogged';
import { bindActionCreators } from 'redux';
import { Typography, Button, Grid } from "@material-ui/core";
import { Link } from 'react-router-dom';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';


const useStyles = makeStyles(theme => ({
    root: {
        padding: theme.spacing(2),
        marginBottom: theme.spacing(1),
      },
      button:{
        height:"100%"
      },
      tag:{
        color:"grey",
        fontSize: "13px"
      },
      links:{
        textDecoration:"none",
        position:"relative",
        right:"0"
      },
      divider:{
        marginBottom:"10px"
      }
}));

function ArticlePage(props) {
    const classes = useStyles();
    const article = props.articles.find(item => item.id === parseInt(props.match.params.id));
    const isLogged = props.isLogged;

    let [editMode, setEditMode] = useState({readOnly: true});
    let [editBtnText, setEditBtnText] = useState("Edit");

    function editArticle(){
      if(editBtnText === "Edit"){
        setEditMode({...editMode, readOnly:false});
        setEditBtnText("Save");
      }else{
        setEditMode({...editMode, readOnly:true});
        setEditBtnText("Edit");
        props.changeArticle(props.articles);
      }
    }
    return (
        <Container maxwidth="lg">
            <Paper className={classes.root}>
                <TextField
                  id="articleBody"
                  label={article.title}
                  defaultValue={article.body}
                  className={classes.textField}
                  margin="normal"
                  fullWidth
                  multiline
                  InputProps={editMode}

                />
                <Grid container>
                  <Grid item xs={8} sm={8} md={8} lg={10} xl={10}>
                    <Typography component="p" className={classes.tag}>
                      {article.tags}
                    </Typography>
                  </Grid>  
                  {isLogged ?(
                    <>
                      <Grid item xs={2} sm={2} md={2} lg={1} xl={1}>
                          <Button variant="outlined" fullWidth className={classes.button} color="primary" onClick={event => editArticle()}>{editBtnText}</Button>
                      </Grid>
                      <Grid item xs={2} sm={2} md={2} lg={1} xl={1}>
                        <Link to="/" className={classes.links}>
                          <Button variant="outlined" fullWidth className={classes.button} color="secondary" onClick={event => props.deleteArticle(article.id)}>Delete</Button>
                        </Link>
                      </Grid>
                    </>
                    ):(<></>)
                  }
                </Grid>
            </Paper>
        </Container>
    );
}

const mapStateToProps = createStructuredSelector({
  articles: getAllArticles,
  isLogged: getLogged
})
const mapDispatchToProps =  dispatch => bindActionCreators({
  changeArticle,
},dispatch)

export default connect(mapStateToProps, mapDispatchToProps)(ArticlePage)
