import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { createStructuredSelector } from 'reselect';
import { getUser } from '../../../selectors/getUser';
import { connect } from 'react-redux';
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles(theme => ({
  card: {
    width:"100%"
  },
  media: {
    height: 140,
    width: 140,
    margin:"0px auto",
    borderRadius:"50%",
    backgroundColor:"black",
    marginBottom:theme.spacing(5),
  },
}));

function ProfileCard(props) {
  const classes = useStyles();
  const [editMode, setEditMode] = useState({readOnly: true});
  const [editBtnText, setEditBtnText] = useState("Edit");
  const [state, setState]=useState({
    fname:props.user.User.fname,
    lname:props.user.User.lname,
    email:props.user.User.email,
    password:props.user.User.password,
  });
  return (
    <Card className={classes.card}>
        <CardContent>
            <CardMedia
            className={classes.media}
            image="/logo.jpg"
            title="Contemplative Reptile"
            />
            <TextField
                  id="articleBody"
                  label="First name"
                  defaultValue={state.fname}
                  className={classes.textField}
                  margin="normal"
                  fullWidth
                  multiline
                  InputProps={editMode}
                  InputLabelProps={{
                    className: classes.label,
                }}
                  onChange={event => setState({...state, body:event.target.value})}
                />
          <Typography gutterBottom variant="h5" component="h2">
            {state.fname}
          </Typography>
          <Typography gutterBottom variant="h5" component="h2">
          {state.lname}
          </Typography>
          <Typography gutterBottom variant="h5" component="h2">
          {state.email}
          </Typography>
          <Typography gutterBottom variant="h5" component="h2">
          {state.password}
          </Typography>
        </CardContent>
      <CardActions>
        <Button size="small" color="primary">
          Edit
        </Button>
      </CardActions>
    </Card>
  );
}
const mapStateToProps = createStructuredSelector({
  user: getUser
})

export default connect(mapStateToProps)(ProfileCard)
